package com.peng.saishi.entity;

public class StringIconInfo {

	private String name;
	private int icon;

	public StringIconInfo(String name, int icon) {
		super();
		this.name = name;
		this.icon = icon;
	}

	public String getName() {
		return name;
	}

	public void setName(String name) {
		this.name = name;
	}

	public int getIcon() {
		return icon;
	}

	public void setIcon(int icon) {
		this.icon = icon;
	}

	public StringIconInfo() {
		// TODO Auto-generated constructor stub
	}

}
